
public class Switchcase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String day="Sunday";
		switch (day) {
		case "Sunday":
			System.out.println("this is "+day);
			break;
		case "Monday":
			System.out.println("this is "+day);
		break;

		default:
			System.out.println("default condition");
			break;
		}
		

	}

}
